INSERT INTO encounter (encounter_type, patient_id, location_id, encounter_datetime, creator, date_created, voided, uuid) SELECT (SELECT encounter_type_id FROM encounter_type WHERE name = 'DISPENSATION') AS encounter_type, person_id, location_id, obs_datetime, creator, date_created, voided, uuid() AS uuid FROM obs WHERE concept_id = 165010 AND encounter_id IS NULL AND voided = 0;
UPDATE obs o LEFT JOIN encounter e ON e.encounter_datetime = o.obs_datetime AND patient_id = person_id AND o.date_created = e.date_created SET o.encounter_id = e.encounter_id WHERE o.concept_id IN (164050, 165033, 165040, 165011, 165010) AND o.encounter_id IS NULL;
INSERT INTO encounter_provider (encounter_id, provider_id, encounter_role_id, creator, date_created, voided, uuid) SELECT encounter_id, 1, 1, 1, date_created, voided, uuid() AS uuid FROM encounter WHERE encounter_type = 19 AND encounter_id NOT IN (SELECT encounter_id FROM encounter_provider) AND voided = 0;

