INSERT INTO supply2_product_operation_flux (product_operation_flux_id, product_code_id, operation_id, quantity, related_quantity, related_quantity_label, observation, location_id, uuid) SELECT product_operation_flux_id, product_code_id, operation_id, quantity, related_quantity, related_quantity_label, spof.observation, spo.location_id, spof.uuid FROM supply_product_operation_flux spof, supply_product_operation spo, supply2_product_code spc WHERE spof.operation_id = spo.product_operation_id AND spo.program_id = spc.program_id AND spof.product_id = spc.product_id;
INSERT INTO supply2_product_attribute SELECT DISTINCT spa.product_attribute_id, product_code_id, batch_number, spa.location_id, expiry_date, spa.creator, spa.date_created, spa.changed_by, spa.date_changed, spa.voided, spa.voided_by, spa.date_voided, spa.void_reason, spa.uuid FROM supply_product_attribute spa INNER JOIN supply_product_operation_flux_attribute spofa ON spa.product_attribute_id = spofa.product_attribute_id INNER JOIN supply_product_operation_flux spof ON spofa.operation_flux_id = spof.product_operation_flux_id INNER JOIN supply_product_operation spo ON spof.operation_id = spo.product_operation_id INNER JOIN supply2_product_code spc ON spc.program_id = spo.program_id AND spc.product_id = spof.product_id;
INSERT INTO supply2_product_operation_flux_attribute SELECT * FROM supply_product_operation_flux_attribute;
INSERT INTO supply2_product_attribute_stock (product_attribute_stock_id, product_attribute, operation_id, quantity_in_stock, location_id, creator, date_created, changed_by, date_changed, voided, voided_by, date_voided, void_reason, uuid) SELECT product_attribute_stock_id, product_attribute, operation_id, quantity_in_stock, location_id, creator, date_created, changed_by, date_changed, voided, voided_by, date_voided, void_reason, uuid FROM supply_product_attribute_stock;

